import React from 'react';

function Shop() {
  return (
    <div>
      <section>
        <h2>GYM WEAR</h2>
        <div style={{ display: 'flex', justifyContent: 'center', gap: 16, flexWrap: 'wrap', margin: '24px 0' }}>
          {["GĂNG TAY", "BÌNH NƯỚC", "CLOTHES", "ÁO", "CẦN KIỂNG TẠ", "DÂY NHẢY", "PHỤ KIỆN"].map((name, i) => (
            <div key={i} style={{ textAlign: 'center' }}>
              <img src={`/wear${i+1}.jpg`} alt={name} style={{ width: 64, height: 64, borderRadius: 8, objectFit: 'cover' }} onError={e => {e.target.style.display='none'}} />
              <div>{name}</div>
            </div>
          ))}
        </div>
      </section>
      <section>
        <div style={{ background: '#c8102e', color: '#fff', fontWeight: 700, fontSize: 32, padding: 16, borderRadius: 8, margin: '24px 0' }}>SALE 30/4</div>
        <div style={{ display: 'flex', flexWrap: 'wrap', gap: 12, justifyContent: 'center' }}>
          {[...Array(12)].map((_, i) => (
            <div key={i} style={{ width: 64, height: 64, background: '#eee', borderRadius: 8, margin: 4 }}></div>
          ))}
        </div>
      </section>
      <section style={{ textAlign: 'left', maxWidth: 800, margin: '32px auto' }}>
        <h3>PHỤ KIỆN THỂ THAO, PHÒNG TẬP - CHỈ CÓ Ở HỆ THỐNG CỦA CHÚNG TÔI</h3>
        <p>text</p>
      </section>
      <section className="black-bg">
        <button className="btn">LIÊN HỆ CHÚNG TÔI</button>
      </section>
    </div>
  );
}

export default Shop;
