import React from "react";
import gangtay from '../img/gangtay.jpeg';
import dailung from '../img/dailung.jpg';
import binhnuoc from '../img/binhnuoc.jpg';
import daynhay from '../img/daynhay.webp';
import aothun from '../img/aothun.jpg';
import quanao from '../img/quanao.webp';
import daykhangluc from '../img/daykhangluc.jpg';

const categories = [
  { name: "GĂNG TAY", img: gangtay, bg: "#f8bfc2" },
  { name: "ĐAI", img: dailung, bg: "#bcd6f7" },
  { name: "BÌNH NƯỚC", img: binhnuoc, bg: "#f8bfc2" },
  { name: "GLOVES", img: daynhay, bg: "#ffe07a" },
  { name: "ÁO", img: aothun, bg: "#f8bfc2" },
  { name: "CÂN ĐIỆN TỬ", img: quanao, bg: "#bcd6f7" },
  { name: "DÂY KHÁNG LỰC", img: daykhangluc, bg: "#bcbcf7" },
];

export default function Products() {
  return (
    <div style={{ background: "#fff", minHeight: "100vh", padding: 0 }}>
      {/* TITLE */}
      <div style={{ textAlign: 'center', fontWeight: 700, fontSize: 32, letterSpacing: 2, margin: '32px 0 24px 0' }}>
        GYM WEAR
      </div>
      {/* CATEGORY BAR */}
      <div style={{ display: "flex", flexWrap: 'wrap', justifyContent: "center", gap: 32, maxWidth: 900, margin: '0 auto' }}>
        {categories.slice(0,5).map((cat, idx) => (
          <div key={idx} style={{ textAlign: "center" }}>
            <div style={{ width: 110, height: 90, background: cat.bg, borderRadius: 12, display: 'flex', alignItems: 'center', justifyContent: 'center', margin: '0 auto' }}>
              <img src={cat.img} alt={cat.name} style={{ width: 70, height: 70, objectFit: "contain" }} />
            </div>
            <div style={{ marginTop: 8, fontWeight: 500, fontSize: 15 }}>{cat.name}</div>
          </div>
        ))}
      </div>
      <div style={{ display: "flex", flexWrap: 'wrap', justifyContent: "center", gap: 32, maxWidth: 900, margin: '24px auto 0 auto' }}>
        {categories.slice(5).map((cat, idx) => (
          <div key={idx} style={{ textAlign: "center" }}>
            <div style={{ width: 110, height: 90, background: cat.bg, borderRadius: 12, display: 'flex', alignItems: 'center', justifyContent: 'center', margin: '0 auto' }}>
              <img src={cat.img} alt={cat.name} style={{ width: 70, height: 70, objectFit: "contain" }} />
            </div>
            <div style={{ marginTop: 8, fontWeight: 500, fontSize: 15 }}>{cat.name}</div>
          </div>
        ))}
      </div>
      {/* BANNER IMAGE */}
      <div style={{ width: 700, maxWidth: '95vw', margin: "40px auto 0 auto", borderRadius: 8, overflow: 'hidden' }}>
        <img src={daynhay} alt="Banner" style={{ width: "100%", height: 260, objectFit: 'cover' }} />
      </div>
      {/* SALE BANNER */}
      <div style={{ display: "flex", justifyContent: "center", margin: "32px 0 16px 0", gap: 8 }}>
        {["S", "A", "L", "E", "30/4"].map((l, idx) => (
          <div key={idx} style={{
            background: "#c8102e",
            color: "#fff",
            fontWeight: "bold",
            fontSize: idx === 4 ? 22 : 32,
            width: idx === 4 ? 80 : 60,
            height: 60,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            borderRadius: 4
          }}>{l}</div>
        ))}
      </div>
    </div>
  );
}
